// Check if task exists and set routing flag
const data = $input.first().json;
const exists = data.exists === true;

// Return item with explicit isNew flag
return [{
  json: {
    ...data,
    isNew: !exists
  }
}];

// Check if claim was successful
const data = $input.first().json;
const claimed = data.claimed === true;

// Return item with explicit claimSuccess flag
return [{
  json: {
    ...data,
    claimSuccess: claimed
  }
}];

// Check if status is "onboarding" (case-insensitive)
const body = $input.first().json.body || {};
const historyItems = body.history_items || [];
const newStatus = (historyItems[0]?.after?.status || '').toLowerCase();

console.log('Status check - new status:', newStatus);

if (newStatus === 'onboarding') {
  // TRUE - proceed with onboarding
  return [{ json: { ...$input.first().json, isOnboarding: true } }];
} else {
  // FALSE - skip, will be routed to skip response
  return [{ json: { ...$input.first().json, isOnboarding: false } }];
}

// Extract custom fields from ClickUp task
const task = $input.first().json;
const customFields = task.custom_fields || [];

// UUID v4 generator
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// Extract field value by NAME (case-insensitive) - works across all tasks/lists
const getFieldByName = (name) => {
  const field = customFields.find(f => 
    f.name.toLowerCase().trim() === name.toLowerCase().trim()
  );
  return field?.value || '';
};

// Get field ID by name for later updates
const getFieldIdByName = (name) => {
  const field = customFields.find(f => 
    f.name.toLowerCase().trim() === name.toLowerCase().trim()
  );
  return field?.id || '';
};

// Extract values by field NAME
const companyName = getFieldByName('company_name');
const companyUrl = getFieldByName('company_url');
const replyApiKey = getFieldByName('Reply API Key');
const replyUser = getFieldByName('Reply User');
const replyPassword = getFieldByName('Reply Password');
const clickupApiKey = getFieldByName('Client ClickUp API Key');
const replyWorkspaceId = getFieldByName('reply_workspace_id');
const clientIdFieldId = getFieldIdByName('Client ID');

// Validate required fields
if (!replyApiKey || !replyUser || !replyPassword || !clickupApiKey) {
  throw new Error(`Missing required fields. Found: Reply API Key=${!!replyApiKey}, Reply User=${!!replyUser}, Reply Password=${!!replyPassword}, Client ClickUp API Key=${!!clickupApiKey}`);
}

// Generate client_id
const clientId = generateUUID();

// Parse domain from company_url
// Input: www.n8n-testing.com, https://www.example.com/path
// Output: n8n-testing.com, example.com
function parseDomain(url) {
  if (!url) return null;
  let domain = url.toLowerCase().trim();
  domain = domain.replace(/^https?:\/\//, ''); // Remove protocol
  domain = domain.replace(/^www\./, '');        // Remove www.
  domain = domain.split('/')[0];                // Remove path
  domain = domain.split('?')[0];                // Remove query
  domain = domain.split('#')[0];                // Remove hash
  domain = domain.split(':')[0];                // Remove port
  return domain || null;
}

const expectedDomain = parseDomain(companyUrl);
const expectedDomains = expectedDomain ? [expectedDomain] : [];

console.log('ðŸ†” Generated client_id:', clientId);
console.log('ðŸŒ Company URL:', companyUrl, 'â†’ Domain:', expectedDomain);
console.log('ðŸ”’ Expected domains:', JSON.stringify(expectedDomains));

return {
  json: {
    client_id: clientId,
    client_id_field_id: clientIdFieldId,
    client_name: companyName || task.name || clientId,
    company_url: companyUrl,
    expected_domains: expectedDomains,
    reply_api_key: replyApiKey,
    reply_io_user: replyUser,
    reply_io_password: replyPassword,
    clickup_api_key: clickupApiKey,
    reply_workspace_id: replyWorkspaceId,
    clickup_task_id: task.id,
    github_owner: 'byronmartinez-ballastlane',
    github_repo: 'qtalo-n8n-testing',
    templates_path: 'templates'
  }
};

// Check if client already exists and is active
const clientInfo = $('Extract Client Info').first().json;
const existingClient = $input.first().json;

// If client exists and is active, skip onboarding
if (existingClient.client_id && existingClient.status === 'active') {
  console.log(`â­ï¸ Client ${clientInfo.client_id} already exists with status 'active' - skipping onboarding`);
  return [{
    json: {
      ...clientInfo,
      skip: true,
      reason: 'Client already onboarded',
      existing_client: existingClient
    }
  }];
}

// Client doesn't exist or is not active, proceed with onboarding
console.log(`âœ… Proceeding with onboarding for ${clientInfo.client_id}`);
return [{
  json: {
    ...clientInfo,
    skip: false
  }
}];

const data = $input.first().json;

return {
  json: {
    success: true,
    message: 'Client already onboarded',
    client_id: data.client_id,
    client_name: data.client_name,
    existing_client: data.existing_client
  }
};

