// Merge credential info with client info
const clientInfo = $('Extract Client Info').first().json;
const clickupCred = $('Create ClickUp Credential').first().json;
const replyCred = $('Create Reply Credential').first().json;

return [{
  json: {
    ...clientInfo,
    clickup_credential_id: clickupCred.id,
    clickup_credential_name: clickupCred.name,
    reply_credential_id: replyCred.id,
    reply_credential_name: replyCred.name
  }
}];

// Decode base64 content, inject external JS files, and inject client values for ALL templates
const clientInfo = $('Merge Credential Info').first().json;
const extractInfo = $('Extract Client Info').first().json;
const items = $input.all();

// GitHub raw content URL base
const githubOwner = extractInfo.github_owner;
const githubRepo = extractInfo.github_repo;
const githubRawBase = `https://raw.githubusercontent.com/${githubOwner}/${githubRepo}/main`;

// Cache for fetched JS files to avoid duplicate requests
const jsFileCache = {};

// Helper function to fetch JS file content from GitHub
async function fetchJsFile(filePath) {
  if (jsFileCache[filePath]) {
    return jsFileCache[filePath];
  }

  const url = `${githubRawBase}/${filePath}`;
  console.log(`ðŸ“¥ Fetching JS file: ${url}`);

  try {
    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: url,
      returnFullResponse: false
    });
    jsFileCache[filePath] = response;
    return response;
  } catch (error) {
    console.error(`âŒ Failed to fetch ${filePath}: ${error.message}`);
    throw new Error(`Failed to fetch JS file: ${filePath}`);
  }
}

// Find all {{INJECT:path}} placeholders in a string
function findInjectPlaceholders(str) {
  const regex = /\{\{INJECT:([^}]+)\}\}/g;
  const matches = [];
  let match;
  while ((match = regex.exec(str)) !== null) {
    matches.push({ placeholder: match[0], path: match[1] });
  }
  return matches;
}

// Process each template
const results = [];
for (const item of items) {
  const base64Content = item.json.content;
  const fileName = item.json.name;

  // Decode base64
  let jsonContent = Buffer.from(base64Content, 'base64').toString('utf8');

  // Find and replace all {{INJECT:path}} placeholders with actual JS content
  const placeholders = findInjectPlaceholders(jsonContent);
  console.log(`ðŸ“‹ Found ${placeholders.length} JS injection placeholders in ${fileName}`);

  for (const { placeholder, path } of placeholders) {
    const jsContent = await fetchJsFile.call(this, path);
    // Escape the JS content for JSON embedding (escape backslashes, quotes, newlines)
    const escapedJs = jsContent
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"')
      .replace(/\n/g, '\\n')
      .replace(/\r/g, '\\r')
      .replace(/\t/g, '\\t');
    jsonContent = jsonContent.replace(placeholder, escapedJs);
    console.log(`âœ… Injected ${path}`);
  }

  let workflow = JSON.parse(jsonContent);

  // Determine template type from filename
  let templateKey = 'unknown';
  if (fileName.includes('phase1')) templateKey = 'phase1';
  else if (fileName.includes('phase2')) templateKey = 'phase2';
  else if (fileName.includes('phase3')) templateKey = 'phase3';
  else if (fileName.includes('orchestrator')) templateKey = 'orchestrator';

  // Update workflow name
  const clientIdLower = clientInfo.client_id.toLowerCase();
  const clientIdUpper = clientInfo.client_id.toUpperCase().replace(/-/g, '_');
  workflow.name = `${clientInfo.client_name} - ${workflow.name.replace(/^.*? - /, '')}`;

  // Inject client values into nodes
  workflow.nodes = workflow.nodes.map(node => {
    if (node.parameters) {
      let params = JSON.stringify(node.parameters);
      // CLIENT_ID lowercase for AWS Secrets Manager lookups
      params = params.replace(/\{\{CLIENT_ID\}\}/g, clientIdLower);
      params = params.replace(/\{\{CLIENT_NAME\}\}/g, clientInfo.client_name);
      params = params.replace(/\$vars\.CLIENT_REPLY_API_KEY/g, `$vars.${clientIdUpper}_REPLY_API_KEY`);
      params = params.replace(/\$vars\.CLIENT_CLICKUP_API_KEY/g, `$vars.${clientIdUpper}_CLICKUP_API_KEY`);
      node.parameters = JSON.parse(params);
    }

    // Replace credential placeholders with actual credential IDs
    if (node.credentials) {
      for (const [key, value] of Object.entries(node.credentials)) {
        if (value.id === '{{CLICKUP_CREDENTIAL_ID}}' || value.name === '{{CLICKUP_CREDENTIAL_NAME}}') {
          value.id = clientInfo.clickup_credential_id;
          value.name = clientInfo.clickup_credential_name;
        }
        if (value.id === '{{REPLY_CREDENTIAL_ID}}' || value.name === '{{REPLY_CREDENTIAL_NAME}}') {
          value.id = clientInfo.reply_credential_id;
          value.name = clientInfo.reply_credential_name;
        }
      }
    }

    return node;
  });

  delete workflow.id;

  results.push({
    json: {
      template_key: templateKey,
      file_name: fileName,
      workflow: workflow,
      client_id: clientInfo.client_id,
      client_name: clientInfo.client_name,
      js_files_injected: placeholders.length
    }
  });
}

return results;

// Collect all created workflow IDs from the Create Workflow node
const createItems = $('Create Workflow in n8n').all();
const decodeItems = $('Decode & Inject Client Values').all();
const extractInfo = $('Extract Client Info').first().json;

const workflowIds = {};
const clientId = decodeItems[0]?.json.client_id;
const clientName = decodeItems[0]?.json.client_name;

createItems.forEach((item, index) => {
  const templateKey = decodeItems[index]?.json.template_key;
  const workflowId = item.json.id;

  if (templateKey && workflowId) {
    workflowIds[templateKey] = workflowId;
  }
});

return [{
  json: {
    client_id: clientId,
    client_name: clientName,
    workflow_ids: workflowIds,
    clickup_task_id: extractInfo.clickup_task_id
  }
}];

// Get the orchestrator workflow and inject Phase workflow IDs
const orchestratorWorkflow = $input.first().json;
const collectedIds = $('Collect Workflow IDs').first().json;
const workflowIds = collectedIds.workflow_ids;

// Get the Phase workflow IDs
const phase1Id = workflowIds.phase1 || '';
const phase2Id = workflowIds.phase2 || '';
const phase3Id = workflowIds.phase3 || '';

console.log('Injecting Phase IDs into Orchestrator:');
console.log('- Phase 1:', phase1Id);
console.log('- Phase 2:', phase2Id);
console.log('- Phase 3:', phase3Id);

// Update nodes that have placeholder workflow IDs
const updatedNodes = orchestratorWorkflow.nodes.map(node => {
  if (node.parameters && node.parameters.workflowId) {
    // Replace Phase workflow ID placeholders
    if (node.parameters.workflowId === '{{PHASE1_WORKFLOW_ID}}' || node.name.includes('Phase 1')) {
      node.parameters.workflowId = phase1Id;
      console.log(`Updated ${node.name} with Phase 1 ID: ${phase1Id}`);
    }
    if (node.parameters.workflowId === '{{PHASE2_WORKFLOW_ID}}' || node.name.includes('Phase 2')) {
      node.parameters.workflowId = phase2Id;
      console.log(`Updated ${node.name} with Phase 2 ID: ${phase2Id}`);
    }
    if (node.parameters.workflowId === '{{PHASE3_WORKFLOW_ID}}' || node.name.includes('Phase 3')) {
      node.parameters.workflowId = phase3Id;
      console.log(`Updated ${node.name} with Phase 3 ID: ${phase3Id}`);
    }
  }
  return node;
});

// Prepare updated workflow for API
const updatedWorkflow = {
  name: orchestratorWorkflow.name,
  nodes: updatedNodes,
  connections: orchestratorWorkflow.connections,
  settings: orchestratorWorkflow.settings || { executionOrder: 'v1' }
};

return [{
  json: {
    orchestrator_id: workflowIds.orchestrator,
    updated_workflow: updatedWorkflow,
    workflow_ids: workflowIds,
    client_id: collectedIds.client_id,
    client_name: collectedIds.client_name,
    clickup_task_id: collectedIds.clickup_task_id
  }
}];

// Pass through the collected data for activation step
const injectedData = $('Inject Phase IDs to Orchestrator').first().json;

return [{
  json: {
    client_id: injectedData.client_id,
    client_name: injectedData.client_name,
    workflow_ids: injectedData.workflow_ids,
    clickup_task_id: injectedData.clickup_task_id
  }
}];
