// Validate rotation request from Lambda
const body = $input.first().json.body;

const newJwtToken = body.new_jwt_token;
const credentialName = body.credential_name;

if (!newJwtToken || !credentialName) {
  throw new Error('Missing required: new_jwt_token and credential_name');
}

console.log(`JWT Rotation requested for credential: ${credentialName}`);

return [{
  json: {
    new_jwt_token: newJwtToken,
    credential_name: credentialName
  }
}];

// Find the credential ID by scanning workflows for a credential with the target name
// Also identify all workflows that use this credential
const requestData = $('Validate Request').first().json;
const workflows = $input.all().map(i => i.json);
const targetName = requestData.credential_name;

let foundCredId = null;
let foundCredType = null;
const workflowsUsingCred = [];

for (const wf of workflows) {
  if (!wf.nodes) continue;

  for (const node of wf.nodes) {
    if (!node.credentials) continue;

    for (const [credType, credInfo] of Object.entries(node.credentials)) {
      if (credInfo.name === targetName) {
        foundCredId = credInfo.id;
        foundCredType = credType;

        // Add workflow ID to list if not already there
        if (!workflowsUsingCred.find(w => w === wf.id)) {
          workflowsUsingCred.push(wf.id);
        }
      }
    }
  }
}

if (!foundCredId) {
  throw new Error(`Credential not found in any workflow: ${targetName}`);
}

// Prepare credential data based on type
let credentialData;
if (foundCredType === 'jwtAuth') {
  credentialData = { keyType: 'passphrase', secret: requestData.new_jwt_token, privateKey: '', publicKey: '', algorithm: 'HS256' };
} else {
  credentialData = { name: 'Authorization', value: 'Bearer ' + requestData.new_jwt_token };
}

console.log(`Found credential ID: ${foundCredId} (${targetName}) of type ${foundCredType}`);
console.log(`Found ${workflowsUsingCred.length} workflows using this credential: ${workflowsUsingCred.join(', ')}`);

return [{
  json: {
    old_credential_id: foundCredId,
    old_credential_name: targetName,
    credential_type: foundCredType,
    credential_data_json: JSON.stringify(credentialData),
    new_jwt_token: requestData.new_jwt_token,
    workflow_ids: workflowsUsingCred,
    workflows_count: workflowsUsingCred.length
  }
}];

// Verify credential was transferred successfully
const newCred = $('Create New Credential').first().json;
const transferResult = $input.first().json;

console.log(`New credential ID: ${newCred.id}`);
console.log(`Credential name: ${newCred.name}`);
console.log(`Transfer result:`, JSON.stringify(transferResult));

// If transfer was successful, pass through the credential info
console.log(`Transferred: Credential ${newCred.id} (${newCred.name}) to QTalo project`);

return [{
  json: {
    new_credential_id: newCred.id,
    new_credential_name: newCred.name,
    credential_type: newCred.type,
    transferred: true
  }
}];

// Store credential IDs and output one item per workflow to update
const oldData = $('Find Credential From Workflows').first().json;
const verifyData = $input.first().json;

console.log(`Credential ${verifyData.new_credential_id} verified in project: ${verifyData.project_name}`);

// Output one item per workflow for the loop
const items = oldData.workflow_ids.map(wfId => ({
  json: {
    old_credential_id: oldData.old_credential_id,
    old_credential_name: oldData.old_credential_name,
    credential_type: oldData.credential_type,
    new_credential_id: verifyData.new_credential_id,
    new_credential_name: verifyData.new_credential_name,
    project_id: verifyData.project_id,
    project_name: verifyData.project_name,
    workflow_id: wfId,
    total_workflows: oldData.workflow_ids.length
  }
}));

return items;

// Transform workflow: update credential references and keep only allowed fields
const batchData = $('Loop Over Workflows').first().json;
const workflow = $input.first().json;

const oldCredId = batchData.old_credential_id;
const newCredId = batchData.new_credential_id;
const newCredName = batchData.new_credential_name;

// Update credential references in nodes
const updatedNodes = workflow.nodes.map(node => {
  if (!node.credentials) return node;

  for (const credType of Object.keys(node.credentials)) {
    if (node.credentials[credType].id === oldCredId) {
      node.credentials[credType] = { id: newCredId, name: newCredName };
    }
  }
  return node;
});

// Keep only fields allowed by PUT API
const updatePayload = {
  name: workflow.name,
  nodes: updatedNodes,
  connections: workflow.connections,
  settings: workflow.settings,
  staticData: workflow.staticData
};

console.log(`Transformed workflow ${workflow.id} (${workflow.name}) for update`);

return [{
  json: {
    workflow_id: workflow.id,
    workflow_name: workflow.name,
    update_payload: updatePayload,
    old_credential_id: oldCredId,
    new_credential_id: newCredId,
    total_workflows: batchData.total_workflows
  }
}];

// Record update result and pass back to loop
const transformData = $('Transform Workflow').first().json;
const putResult = $input.first().json;

const success = putResult.id !== undefined;

console.log(`Workflow ${transformData.workflow_id} update: ${success ? 'SUCCESS' : 'FAILED'}`);

return [{
  json: {
    workflow_id: transformData.workflow_id,
    workflow_name: transformData.workflow_name,
    status: success ? 'updated' : 'error',
    error: success ? null : (putResult.message || 'Unknown error'),
    old_credential_id: transformData.old_credential_id,
    new_credential_id: transformData.new_credential_id,
    total_workflows: transformData.total_workflows
  }
}];

// Aggregate all update results
const results = $input.all().map(i => i.json);

const updated = results.filter(r => r.status === 'updated').length;
const errored = results.filter(r => r.status === 'error').length;

// Get credential IDs from first result
const firstResult = results[0] || {};

console.log(`Workflow updates complete: ${updated} updated, ${errored} errors`);

return [{
  json: {
    old_credential_id: firstResult.old_credential_id,
    new_credential_id: firstResult.new_credential_id,
    workflows_updated: updated,
    workflows_errored: errored,
    results: results
  }
}];

// Final response
const updateData = $('Aggregate Results').first().json;
const deleteResult = $input.first().json;

return [{
  json: {
    success: true,
    old_credential_id: updateData.old_credential_id,
    old_credential_deleted: !deleteResult.error,
    new_credential_id: updateData.new_credential_id,
    workflows_updated: updateData.workflows_updated,
    workflows_errored: updateData.workflows_errored,
    details: updateData.results
  }
}];
