// Parse API response and extract clients
const response = $input.first().json;
const clients = response.clients || [];

if (clients.length === 0) {
  console.log('No clients found');
  return [];
}

// Filter only active clients that need workflow deployment
const activeClients = clients.filter(c => c.status === 'active');

console.log(`Found ${activeClients.length} active clients out of ${clients.length} total`);

return activeClients.map(c => ({ json: c }));

// Check if client needs workflow deployment
const CURRENT_TEMPLATE_VERSION = '2.0.0';
const client = $input.item.json;

// Needs update if: different version OR missing any workflow IDs OR missing credentials
const workflowIds = client.workflow_ids || {};
const credentialIds = client.credential_ids || {};
const hasAllWorkflows = ['main-orchestrator', 'phase1-import-hygiene', 'phase2-signatures', 'phase3-standardize']
  .every(w => workflowIds[w]);
const hasAllCredentials = credentialIds.clickup && credentialIds.reply;

const needsUpdate = client.template_version !== CURRENT_TEMPLATE_VERSION || !hasAllWorkflows || !hasAllCredentials;

console.log(`Client ${client.client_id}: version=${client.template_version}, hasAllWorkflows=${hasAllWorkflows}, hasAllCredentials=${hasAllCredentials}, needsUpdate=${needsUpdate}`);

return {
  json: {
    ...client,
    needs_update: needsUpdate,
    current_template_version: CURRENT_TEMPLATE_VERSION
  }
};

// Create n8n CREDENTIALS for this client (not just variables)
const clientData = $('Needs Update?').first().json;
const credentialsResponse = $input.first().json;
const credentials = credentialsResponse.credentials || {};
const n8nApiKey = $vars.N8N_API_KEY || '';

const clientId = clientData.client_id;
const clientName = clientData.client_name || clientId;

// ClickUp credential name
const clickupCredName = `${clientName}-ClickUp-API`;
const replyCredName = `${clientName}-Reply-API`;

let clickupCredentialId = null;
let replyCredentialId = null;

// Create ClickUp credential
try {
  // First try to find existing credential by name
  const existingCreds = await this.helpers.request({
    method: 'GET',
    uri: 'https://qtalospace.app.n8n.cloud/api/v1/credentials',
    headers: { 'X-N8N-API-KEY': n8nApiKey },
    json: true
  });

  const existingClickup = existingCreds.data?.find(c => c.name === clickupCredName);
  const existingReply = existingCreds.data?.find(c => c.name === replyCredName);

  if (existingClickup) {
    clickupCredentialId = existingClickup.id;
    console.log(`Found existing ClickUp credential: ${clickupCredentialId}`);
  } else if (credentials.clickup_api_key) {
    // Create new ClickUp credential
    const clickupCred = await this.helpers.request({
      method: 'POST',
      uri: 'https://qtalospace.app.n8n.cloud/api/v1/credentials',
      headers: {
        'X-N8N-API-KEY': n8nApiKey,
        'Content-Type': 'application/json'
      },
      body: {
        name: clickupCredName,
        type: 'httpHeaderAuth',
        data: {
          name: 'Authorization',
          value: credentials.clickup_api_key
        }
      },
      json: true
    });
    clickupCredentialId = clickupCred.id;
    console.log(`Created ClickUp credential: ${clickupCredentialId}`);
  }

  if (existingReply) {
    replyCredentialId = existingReply.id;
    console.log(`Found existing Reply credential: ${replyCredentialId}`);
  } else if (credentials.reply_api_key) {
    // Create new Reply credential
    const replyCred = await this.helpers.request({
      method: 'POST',
      uri: 'https://qtalospace.app.n8n.cloud/api/v1/credentials',
      headers: {
        'X-N8N-API-KEY': n8nApiKey,
        'Content-Type': 'application/json'
      },
      body: {
        name: replyCredName,
        type: 'httpHeaderAuth',
        data: {
          name: 'X-Api-Key',
          value: credentials.reply_api_key
        }
      },
      json: true
    });
    replyCredentialId = replyCred.id;
    console.log(`Created Reply credential: ${replyCredentialId}`);
  }

} catch (error) {
  console.error('Error creating credentials:', error.message);
}

return [{
  json: {
    client_id: clientId,
    client_name: clientName,
    clickup_space_id: clientData.clickup_space_id,
    workflow_ids: clientData.workflow_ids || {},
    credential_ids: {
      clickup: clickupCredentialId,
      reply: replyCredentialId
    },
    credential_names: {
      clickup: clickupCredName,
      reply: replyCredName
    },
    reply_workspace_id: credentials.reply_workspace_id || 'default',
    api_keys: {
      clickup: credentials.clickup_api_key,
      reply: credentials.reply_api_key
    }
  }
}];

// Prepare workflow deployments with client-specific credential IDs
const data = $input.first().json;

// Templates to deploy
const templates = [
  { templateName: 'phase1-import-hygiene', displayName: 'Phase 1: Import & Hygiene' },
  { templateName: 'phase2-signatures', displayName: 'Phase 2: Signatures & Opt-Outs' },
  { templateName: 'phase3-standardize', displayName: 'Phase 3: Standardize Workspace' },
  { templateName: 'main-orchestrator', displayName: 'Main Orchestrator' }
];

return templates.map(t => ({
  json: {
    client_id: data.client_id,
    client_name: data.client_name,
    clickup_space_id: data.clickup_space_id,
    workflow_ids: data.workflow_ids || {},
    credential_ids: data.credential_ids,
    credential_names: data.credential_names,
    api_keys: data.api_keys,
    template_name: t.templateName,
    workflow_name: `${data.client_name} - ${t.displayName}`,
    reply_workspace_id: data.reply_workspace_id
  }
}));

// Build workflow from template with client-specific CREDENTIALS
const item = $input.item.json;
const n8nApiKey = $vars.N8N_API_KEY || '';

// Template base URL (GitHub raw) - using new testing repo
const templateBaseUrl = 'https://raw.githubusercontent.com/byronmartinez-ballastlane/qtalo-n8n-testing/main/templates';

// Get the existing workflow ID if updating
const existingWorkflowId = item.workflow_ids[item.template_name];
const action = existingWorkflowId ? 'update' : 'create';

let workflowPayload;

try {
  // Try to fetch the template from GitHub
  const templateUrl = `${templateBaseUrl}/${item.template_name}.template.json`;
  const template = await this.helpers.request({
    method: 'GET',
    uri: templateUrl,
    json: true
  });

  // Convert template to string, do replacements, convert back
  let templateStr = JSON.stringify(template);

  // Replace credential ID placeholders with actual credential IDs
  templateStr = templateStr.replace(/\{\{CLICKUP_CREDENTIAL_ID\}\}/g, item.credential_ids.clickup || '');
  templateStr = templateStr.replace(/\{\{REPLY_CREDENTIAL_ID\}\}/g, item.credential_ids.reply || '');

  // Replace client name placeholder
  templateStr = templateStr.replace(/\{\{CLIENT_NAME\}\}/g, item.client_name);

  // Replace credential name placeholders
  templateStr = templateStr.replace(/\{\{CLICKUP_CREDENTIAL_NAME\}\}/g, item.credential_names.clickup || 'ClickUp API');
  templateStr = templateStr.replace(/\{\{REPLY_CREDENTIAL_NAME\}\}/g, item.credential_names.reply || 'Reply.io API');

  // Replace API key placeholders for Code nodes that need direct access
  // Format: HARDCODED_CLICKUP_API_KEY and HARDCODED_REPLY_API_KEY
  templateStr = templateStr.replace(/HARDCODED_CLICKUP_API_KEY/g, item.api_keys.clickup || '');
  templateStr = templateStr.replace(/HARDCODED_REPLY_API_KEY/g, item.api_keys.reply || '');

  workflowPayload = JSON.parse(templateStr);
  workflowPayload.name = item.workflow_name;
  workflowPayload.tags = [item.client_id];

} catch (fetchError) {
  console.log(`Could not fetch template: ${fetchError.message}`);

  // Fallback: create minimal workflow placeholder
  workflowPayload = {
    name: item.workflow_name,
    nodes: [{
      parameters: {},
      id: 'placeholder',
      name: 'Placeholder - Template fetch failed',
      type: 'n8n-nodes-base.noOp',
      typeVersion: 1,
      position: [240, 300]
    }],
    connections: {},
    active: false,
    settings: { executionOrder: 'v1' },
    tags: [item.client_id]
  };
}

return {
  json: {
    client_id: item.client_id,
    client_name: item.client_name,
    template_name: item.template_name,
    workflow_name: item.workflow_name,
    existing_workflow_id: existingWorkflowId || null,
    action: action,
    credential_ids: item.credential_ids,
    workflow_payload: workflowPayload
  }
};

// Process API response and prepare for DynamoDB update
const response = $input.first().json;
const previousData = $('Build Workflow from Template').item.json;

return {
  json: {
    client_id: previousData.client_id,
    template_name: previousData.template_name,
    workflow_name: previousData.workflow_name,
    workflow_id: response.id || previousData.existing_workflow_id,
    credential_ids: previousData.credential_ids,
    action: previousData.action === 'update' ? 'updated' : 'created',
    status: 'success'
  }
};

// Client skipped (no update needed)
const client = $input.item.json;

return {
  json: {
    client_id: client.client_id,
    client_name: client.client_name,
    status: 'skipped',
    reason: 'Already up to date'
  }
};

// Summarize all results
const allResults = $input.all().map(i => i.json);

const summary = {
  total_clients: allResults.length,
  updated: allResults.filter(r => r.status === 'success' || r.action).length,
  skipped: allResults.filter(r => r.status === 'skipped').length,
  errors: allResults.filter(r => r.status === 'error').length,
  timestamp: new Date().toISOString(),
  details: allResults
};

console.log('Replication Summary:', JSON.stringify(summary, null, 2));

return [{ json: summary }];
