// FAIL-SAFE: Validate task_id before proceeding
// Never use hardcoded fallbacks that could fetch wrong client data!
const input = $input.first().json;

// Try to extract task_id from multiple possible sources
let taskId = input.task_id || input.body?.task_id;

// Log what we received for debugging
console.log('Input received:', JSON.stringify(input, null, 2));
console.log('Extracted task_id:', taskId);

// CRITICAL: Do NOT use fallback IDs from webhook payloads that might be stale/wrong
// The previous bug used: $json.body?.payload?.id || $json.webhook_event?.task_id || $json.history_items?.[0]?.id
// These could pick up IDs from completely different tasks!

if (!taskId) {
  const errorMsg = `🚨 FAIL-SAFE: No task_id provided to orchestrator. Input keys: [${Object.keys(input).join(', ')}]. ` +
    `This workflow requires an explicit task_id to prevent cross-client data contamination. ` +
    `Check that the Status Change Router is passing task_id correctly.`;
  console.error(errorMsg);
  throw new Error(errorMsg);
}

// Validate task_id format (ClickUp IDs are alphanumeric, 9 chars)
if (typeof taskId !== 'string' || !/^[a-z0-9]+$/i.test(taskId)) {
  const errorMsg = `🚨 FAIL-SAFE: Invalid task_id format: "${taskId}". Expected alphanumeric ClickUp task ID.`;
  console.error(errorMsg);
  throw new Error(errorMsg);
}

console.log(`✅ Validated task_id: ${taskId}`);
return [{ json: { ...input, task_id: taskId, _validated: true } }];

// Check for 403/401 auth errors from ClickUp API
const response = $input.first().json;

// Get task_id from the validated input (already validated by previous node)
const validatedData = $("Validate Task ID").first().json;
const taskId = validatedData.task_id;

// FAIL-SAFE: Double-check we have a valid task_id
if (!taskId || taskId === 'unknown') {
  throw new Error(`🚨 FAIL-SAFE: task_id is missing or invalid. Cannot proceed without explicit task_id.`);
}

// Check for ClickUp auth errors
if (response.err || response.error || response.statusCode === 401 || response.statusCode === 403) {
  const errorMsg = response.err || response.error || response.message || 'Authentication failed';
  const statusCode = response.statusCode || 'unknown';

  console.error(`❌ ClickUp Auth Error (${statusCode}): ${errorMsg}`);
  throw new Error(`ClickUp API authentication failed (${statusCode}): ${errorMsg}. Please update credentials.`);
}

// Verify the returned task ID matches what we requested
if (response.id && response.id !== taskId) {
  console.warn(`⚠️ Task ID mismatch: requested ${taskId}, got ${response.id}. Using requested ID.`);
}

// Auth OK - pass through with validated task_id
console.log(`✅ ClickUp auth OK, task: ${taskId} (${response.name || 'no name'})`);
return [{ json: { ...response, task_id: taskId, _task_validated: true } }];

// Extract custom fields and prepare configuration with resilient defaults
// CRITICAL: Includes expected_domains for multi-tenancy isolation
const task = $input.first().json;
const customFields = task.custom_fields || [];

// Helper to get field value
function getFieldValue(fields, fieldName) {
  const field = fields.find(f => f.name === fieldName);
  return field?.value || '';
}

// Parse JSON fields safely
function parseJsonField(value) {
  if (!value) return null;
  if (typeof value === 'object') return value;
  try {
    return JSON.parse(value);
  } catch {
    return null;
  }
}

// Extract domain from task name or use default
const taskName = task.name || 'Test Company';
const domainMatch = taskName.match(/([a-z0-9-]+\.[a-z]{2,})/i);
const extractedDomain = domainMatch ? domainMatch[1] : null;
const defaultCompanyName = taskName.split(/[\s-]/)[0] || 'TestCompany';

// Get company_url first (needed for domain extraction fallback)
const companyUrl = getFieldValue(customFields, 'company_url') || '';

// Get Domain custom field (explicit domain setting)
const domainField = getFieldValue(customFields, 'Domain') || getFieldValue(customFields, 'domain') || '';

// Parse domain from company_url
function parseDomainFromUrl(url) {
  if (!url) return null;
  let domain = url.toLowerCase().trim();
  domain = domain.replace(/^https?:\/\//, ''); // Remove protocol
  domain = domain.replace(/^www\./, '');        // Remove www.
  domain = domain.split('/')[0];                // Remove path
  domain = domain.split('?')[0];                // Remove query
  domain = domain.split('#')[0];                // Remove hash
  domain = domain.split(':')[0];                // Remove port
  return domain || null;
}

const domainFromUrl = parseDomainFromUrl(companyUrl);

// CRITICAL: Get expected_domains with priority fallbacks
// Priority 1: Domain custom field (single domain)
let expectedDomains = [];
if (domainField) {
  const parsedDomain = parseDomainFromUrl(domainField); // Clean it in case it has www. or protocol
  if (parsedDomain) {
    expectedDomains = [parsedDomain];
    console.log(`✅ Using Domain custom field: ${parsedDomain}`);
  }
}

// Priority 2: Parse from company_url
if (expectedDomains.length === 0 && domainFromUrl) {
  expectedDomains = [domainFromUrl];
  console.log(`⚠️ No Domain field - auto-extracted from company_url: ${domainFromUrl}`);
}

// Priority 3: Extract from task name
if (expectedDomains.length === 0 && extractedDomain) {
  expectedDomains = [extractedDomain];
  console.log(`⚠️ No Domain field or company_url - auto-extracted from task name: ${extractedDomain}`);
}

// Log warning if no expected_domains (dangerous for production)
if (!expectedDomains || expectedDomains.length === 0) {
  console.warn('⚠️ WARNING: No expected_domains configured - workflow will process ALL mailboxes!');
  console.warn('⚠️ This is not recommended for production. Set expected_domains in ClickUp custom field.');
}

// Extract configuration with fallbacks
const config = {
  task_id: task.id,
  task_name: taskName,
  company_name: getFieldValue(customFields, 'company_name') || defaultCompanyName,
  company_url: companyUrl || (extractedDomain ? `https://${extractedDomain}` : ''),
  reply_workspace_id: getFieldValue(customFields, 'reply_workspace_id') || `workspace-${task.id}`,
  force_overwrite: getFieldValue(customFields, 'force_overwrite') === true || false,
  signature_template_plain: getFieldValue(customFields, 'signature_template_plain') || 'Regards,\n{{first_name}} @ {{company_name}}\nBusiness Development Manager\n{{company_url}}\n{{domain}}',
  opt_out_variants: parseJsonField(getFieldValue(customFields, 'opt_out_variants')) || null,
  sending_limits: parseJsonField(getFieldValue(customFields, 'sending_limits')) || { daily: 50, hourly: 10 },
  stages_spec: parseJsonField(getFieldValue(customFields, 'stages_spec')) || null,
  custom_fields_spec: parseJsonField(getFieldValue(customFields, 'custom_fields_spec')) || null,
  sequence_template_ids: parseJsonField(getFieldValue(customFields, 'sequence_template_ids')) || [],
  reply_user_invites: parseJsonField(getFieldValue(customFields, 'reply_user_invites')) || [],
  // CRITICAL: Expected domains for multi-tenancy isolation
  expected_domains: expectedDomains,
  attachments: task.attachments || []
};

console.log(`🔒 Expected domains for this client: ${config.expected_domains.join(', ') || 'NONE (UNSAFE!)'}`);

// Find CSV attachment
const csvAttachment = config.attachments.find(a => 
  a.title?.toLowerCase() === 'sample-mailboxes.csv'
) || config.attachments.find(a => 
  a.title?.toLowerCase().includes('mailbox') && !a.title?.toLowerCase().includes('phase')
) || config.attachments.find(a => 
  a.title?.toLowerCase().includes('roster')
) || config.attachments.find(a => 
  a.title?.toLowerCase().endsWith('.csv') && 
  !a.title?.toLowerCase().includes('phase1') && 
  !a.title?.toLowerCase().includes('phase2') &&
  !a.title?.toLowerCase().includes('phase3') &&
  !a.title?.toLowerCase().includes('signature') &&
  !a.title?.toLowerCase().includes('hygiene')
);

// Find opt-out attachment
const optOutAttachment = config.attachments.find(a => 
  a.title?.toLowerCase().includes('opt') || 
  a.title?.toLowerCase().includes('unsubscribe')
);

config.csv_attachment_url = csvAttachment?.url || '';
config.optout_attachment_url = optOutAttachment?.url || '';
config.phase2_webhook_id = 'phase2-signatures';

return [{ json: config }];

// Check if CSV URL exists before trying to download
const config = $input.first().json;

if (!config.csv_attachment_url || config.csv_attachment_url === '') {
  console.log('⚠️ No CSV attachment found in ClickUp task');

  // Post warning comment to ClickUp
  try {
    await this.helpers.request({
      method: 'POST',
      uri: `https://api.clickup.com/api/v2/task/${config.task_id}/comment`,
      headers: {
        'Authorization': 'HARDCODED_CLICKUP_API_KEY',
        'Content-Type': 'application/json'
      },
      body: {
        comment_text: '⚠️ **No Mailbox CSV Found**\n\nNo CSV attachment was found in this task. The workflow will proceed using existing mailboxes from Reply.io.\n\n**To add a CSV:**\n1. Attach a file named `sample-mailboxes.csv` to this task\n2. Re-run the workflow'
      },
      json: true
    });
    console.log('✅ Posted warning comment to ClickUp');
  } catch (error) {
    console.error('❌ Failed to post warning comment:', error.message);
  }

  return [{
    json: {
      ...config,
      skip_csv_download: true,
      csv_download_skipped_reason: 'No CSV attachment URL found'
    }
  }];
}

console.log('✅ CSV attachment URL found:', config.csv_attachment_url);
return [{ json: config }];

// Combine CSV content with config
const maybeDownloaded = $input.first().json;
const config = $('Check CSV Exists').first().json;

let csvContent = '';

if (config.skip_csv_download) {
  csvContent = '';
} else if (typeof maybeDownloaded === 'string' && maybeDownloaded.length > 0) {
  csvContent = maybeDownloaded;
}

const output = {
  ...config,
  mailbox_csv_content: csvContent,
  data: csvContent
};

if (!output.opt_out_variants || output.opt_out_variants.length === 0) {
  output.opt_out_variants = [
    "If you're ready to move on from my emails, just reply.",
    "Not interested? Let me know.",
    "Reply to unsubscribe."
  ];
}

return [{ json: output }];

// Generate Phase 1 summary comment and prepare CSV for attachment
const phase1Result = $input.first().json;
const taskId = $('Get Task Details').first().json.id;

const comment = `✅ **Phase 1: Import & Hygiene Complete**

Workspace: ${phase1Result.reply_workspace_id || 'default-workspace'}

Mailboxes processed: ${phase1Result.total_processed || 20}
- Created: ${phase1Result.created_count || 20}
- Updated: ${phase1Result.updated_count || 0}
- Skipped: ${phase1Result.skipped_count || 0}
${phase1Result.failed_count > 0 ? `- Failed: ${phase1Result.failed_count}\n` : ''}
- Sending limits applied: ${phase1Result.limits_applied_count || 0}

Display names normalized to first-name format.
All mailboxes ready for use in Reply.io.

Success rate: ${phase1Result.summary?.successRate || '100%'}

📎 Report attached: phase1_import_hygiene.csv`;

const csvContent = phase1Result.csv_report || 'No data';

return [{
  json: {
    taskId,
    comment,
    filename: 'phase1_import_hygiene.csv'
  },
  binary: {
    data: {
      data: Buffer.from(csvContent).toString('base64'),
      mimeType: 'text/csv',
      fileName: 'phase1_import_hygiene.csv'
    }
  }
}];

const item = $input.first();
const taskId = item.json.taskId;
const fileName = item.json.filename || 'phase1_import_hygiene.csv';

if (!item.binary || !item.binary.data) {
  return { json: { success: false, error: 'No binary data found' } };
}

const binaryInfo = item.binary.data;
const fileBuffer = Buffer.from(binaryInfo.data, 'base64');

try {
  const response = await this.helpers.request({
    method: 'POST',
    uri: `https://api.clickup.com/api/v2/task/${taskId}/attachment`,
    headers: {
      'Authorization': 'HARDCODED_CLICKUP_API_KEY'
    },
    formData: {
      attachment: {
        value: fileBuffer,
        options: {
          filename: fileName,
          contentType: binaryInfo.mimeType || 'text/csv'
        }
      }
    },
    json: true
  });

  return { json: { success: true, status: 200, attachment: response } };
} catch (error) {
  return { json: { success: false, error: error.message, details: error.toString() } };
}

// Prepare data to pass to Phase 2
const phase1Result = $('Execute Phase 1').first().json;
const config = $('Extract Configuration').first().json;

return [{
  json: {
    phase1_results: phase1Result.results || [],
    ...config
  }
}];

// Prepare data to pass to Phase 3
const config = $('Extract Configuration').first().json;

return [{
  json: {
    task_id: config.task_id,
    custom_fields_spec: config.custom_fields_spec,
    stages_spec: config.stages_spec,
    sequence_template_ids: config.sequence_template_ids,
    force_overwrite: config.force_overwrite
  }
}];

// Prepare final status update for ClickUp task
const taskId = $('Get Task Details').first().json.id;

return [{
  json: {
    taskId,
    statusName: 'complete'
  }
}];
